const headerBurger=document.getElementById("burger"),headerMenu=document.getElementById("header-nav"),targetTime=(headerBurger.addEventListener("click",function(e){e.target.classList.toggle("burger-lines-active"),headerMenu.classList.toggle("navigation-active")}),new Date),timerInterval=(targetTime.setHours(targetTime.getHours()+2),setInterval(updateTimer,1e3));function updateTimer(){var e,t,n=new Date,n=targetTime-n;n<=0?(clearInterval(timerInterval),document.getElementById("timer").innerText="Time's up!"):(e=Math.floor(n/36e5),t=Math.floor(n%36e5/6e4),n=Math.floor(n%6e4/1e3),document.getElementById("countdown").innerText=e+`:${t}:`+n)}const phoneInput=document.getElementById("phone-input");function formatDate(e){return`Order date: ${String(e.getDate()).padStart(2,"0")}.${String(e.getMonth()+1).padStart(2,"0")}.`+e.getFullYear()}phoneInput.addEventListener("input",function(){let e=phoneInput.value.replace(/\D/g,"");var t="+"+(e=e.startsWith("380")?e:"380"+e).substr(0,2)+"("+e.substr(2,3)+")"+e.substr(5,3)+"-"+e.substr(8,2)+"-"+e.substr(10,2);phoneInput.value=t});const today=new Date;document.getElementById("order-date").innerText=formatDate(today);